mapper.select:
SELECT ibutcher, name, location FROM t_butcher_shop
name like CONCAT('%',#{search},'%')
LIMIT #{startIdx}, #{rowCount}

mapper.insert:
INSERT INTO t_but_review
SET iuser = #{iuser}
, ibutcher = #{ibutcher}
, star = #{star}

, review = #{review}

mapper.select:
SELECT ibutcher,menu,price,pic
FROM t_but_menu
WHERE ibutcher = #{ibutcher}

SELECT ibutcher, name, location, x, y,created_at as createdAt
FROM t_butcher_shop
WHERE ibutcher = #{ibutcher}

SELECT
    iuser
FROM t_but_bookmark
where iuser= #{iuser}
AND ibutcher= #{ibutcher}

mapper.delete:
DELETE
FROM t_but_bookmark
WHERE iuser= #{iuser}
AND ibutcher= #{ibutcher}
