mapper.update:
update t_community
set title = #{title},
contents = #{contents}
where iboard = #{iboard}
and iuser = #{iuser}

update t_commu_pics
set pic = #{pic}
where iboard = #{iboard}

mapper.select:
SELECT a.iboard, a.iuser, b.nickname AS name,b.pic as writerPic, a.title, a.contents, date(a.created_at) AS createdAt FROM t_community a JOIN t_user b ON a.iuser = b.iuser
a.title like CONCAT('%',#{search},'%')
ORDER BY a.created_at DESC limit #{startIdx}, #{rowCount}

select iboard, pic
from t_commu_pics
where iboard in (

    #{iboard}

)

mapper.delete:
delete from t_community
where iuser = #{iuser}
and iboard = #{iboard}

DELETE
FROM t_community_comment
WHERE iuser= #{iuser}
AND iboard= #{iboard}

