mapper.update:
update t_community
set title = #{title},
contents = #{contents}
where iboard = #{iboard}
and iuser = #{iuser}

update t_commu_pics
set pic = #{pic}
where iboard = #{iboard}

mapper.select:
select iboard, pic
from t_commu_pics
where iboard in (

    #{iboard}

)

mapper.delete:
delete from t_community
where iuser = #{iuser}
and iboard = #{iboard}

mapper.select:
