mapper.update:
UPDATE
t_reservation
SET
    confirm= 1
WHERE ireser= #{ireser}
AND iuser= #{iuser}

UPDATE
t_pickup
SET
    confirm= 1
WHERE ipickup= #{ipickup}
AND iuser= #{iuser}

mapper.select:
SELECT
    *
FROM t_reservation
WHERE iuser= #{iuser}
AND ishop= #{ishop}
AND date= #{date}

SELECT
    *
FROM t_pickup
WHERE iuser= #{iuser}
AND ibutcher= #{ibutcher}
AND date= #{date}

SELECT
    *
FROM t_pickup_menu
WHERE ipickup= #{ipickup}
AND ibut_menu= #{ibutMenu}

SELECT
    confirm
FROM t_reservation
WHERE iuser= #{iuser}
AND ireser= #{ireser}

SELECT
    confirm
FROM t_pickup
WHERE iuser= #{iuser}
AND ipickup= #{ipickup}
