mapper.select:
SELECT ishop, name, location FROM t_shop
name like CONCAT('%',#{search},'%')
LIMIT #{startIdx}, #{rowCount}

SELECT ishop, pic
FROM t_shop_pic
WHERE ishop in (

    #{ishop}

)

SELECT ishop,menu,price,pic
FROM t_shop_menu
WHERE ishop = #{ishop}

SELECT
    iuser
FROM t_shop_bookmark
where iuser= #{iuser}
AND ishop= #{ishop}

mapper.delete:
DELETE
FROM t_shop_bookmark
WHERE iuser= #{iuser}
AND ishop= #{ishop}
